package in.clubgo.app.adapter;import android.content.Context;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.RelativeLayout;import android.widget.TextView;import com.defuzed.clubgo.R;import in.clubgo.app.modal.ModalBooked;import com.squareup.picasso.Picasso;import java.util.List;/** * Created by JItendra Soam on 28/3/16. */public class TicketsAdapter extends RecyclerView.Adapter<TicketsAdapter.CustomViewHolder> {    private int pos;    private Context context;    private List<ModalBooked> mDataList;    private static MyClickListener myClickListener;    public TicketsAdapter() {    }    public TicketsAdapter(int pos, Context context, List<ModalBooked> mDataList) {        this.pos = pos;        this.context = context;        this.mDataList = mDataList;    }    public class CustomViewHolder extends RecyclerView.ViewHolder implements View.OnClickListener {        private ImageView ivImage, ivShare;        private RelativeLayout relativeLayout;        private LinearLayout linearLayout, llNormal, llVIp;        private TextView tvTitle, tvName, tvLocation, tvDate, tvEntry, tvBookId,                tvCouple, tvFemale, tvMale, tvVIpName, tvVIpDes, tvPrice, tvReview;        public CustomViewHolder(View itemView) {            super(itemView);            linearLayout = (LinearLayout) itemView.findViewById(R.id.linearLayout);            llNormal = (LinearLayout) itemView.findViewById(R.id.ll_Normal);            llVIp = (LinearLayout) itemView.findViewById(R.id.ll_VIP);            relativeLayout = (RelativeLayout) itemView.findViewById(R.id.relativeLayout);            ivImage = (ImageView) itemView.findViewById(R.id.ticket_ivEventImage);            tvTitle = (TextView) itemView.findViewById(R.id.ticket_txtTitle);            tvName = (TextView) itemView.findViewById(R.id.ticket_txtName);            tvLocation = (TextView) itemView.findViewById(R.id.ticket_txtLocation);            tvDate = (TextView) itemView.findViewById(R.id.ticket_txtDate);            tvEntry = (TextView) itemView.findViewById(R.id.ticket_txtLastEntry);            tvBookId = (TextView) itemView.findViewById(R.id.ticket_txtBookId);            tvCouple = (TextView) itemView.findViewById(R.id.ticket_txtCouple);            tvFemale = (TextView) itemView.findViewById(R.id.ticket_txtFemale);            tvMale = (TextView) itemView.findViewById(R.id.ticket_txtMale);            tvVIpName = (TextView) itemView.findViewById(R.id.ticket_txtVipName);            tvVIpDes = (TextView) itemView.findViewById(R.id.ticket_txtVipDescription);            tvPrice = (TextView) itemView.findViewById(R.id.ticket_txtPrice);            tvReview = (TextView) itemView.findViewById(R.id.ticket_txtReview);            ivShare = (ImageView) itemView.findViewById(R.id.ticket_ivShare);            ivShare.setOnClickListener(this);            tvReview.setOnClickListener(this);            relativeLayout.setOnClickListener(this);            if (pos == 1) {                tvReview.setTextColor(context.getResources().getColor(R.color.indicator));            } else {                tvEntry.setVisibility(View.VISIBLE);            }        }        @Override        public void onClick(View view) {            switch (view.getId()) {                case R.id.relativeLayout:                    myClickListener.onItemClick(view, mDataList.get(getPosition()).getEventId());                    break;                case R.id.ticket_txtReview:                    myClickListener.onItemClick(view, mDataList.get(getPosition()).getIds());                    break;                case R.id.ticket_ivShare:                    String str = "Hi, I've booked tickets for " + mDataList.get(getPosition()).getEventname() + ", at " +                            mDataList.get(getPosition()).getVenuelocation() + " for " +                            mDataList.get(getPosition()).getDate() + ", using the ClubGo App. Download the app now to book tickets yourself -http://www.clubgo.in/";                    myClickListener.onItemClick(view, str);                    break;                default:                    break;            }        }    }    @Override    public CustomViewHolder onCreateViewHolder(ViewGroup parent, int viewType) {        View layoutView = LayoutInflater.from(parent.getContext()).inflate(R.layout.view_tickets, null);        CustomViewHolder rcv = new CustomViewHolder(layoutView);        return rcv;    }    @Override    public void onBindViewHolder(CustomViewHolder holder, int position) {        if (position % 2 == 1) {            holder.linearLayout.setBackgroundColor(context.getResources().getColor(R.color.gray));        } else {            holder.linearLayout.setBackgroundColor(context.getResources().getColor(R.color.white));        }        if (mDataList.get(position).getType().equals("normal")) {            holder.llVIp.setVisibility(View.GONE);            holder.llNormal.setVisibility(View.VISIBLE);        } else if (mDataList.get(position).getType().equals("vip")) {            holder.llVIp.setVisibility(View.VISIBLE);            holder.llNormal.setVisibility(View.GONE);            holder.tvEntry.setVisibility(View.GONE);        }        Picasso.with(context)                .load(mDataList.get(position).getImage())                .placeholder(R.drawable.image_holder)                .fit().centerInside()                .into(holder.ivImage);        holder.tvName.setText(mDataList.get(position).getName());        holder.tvTitle.setText(mDataList.get(position).getEventname());        holder.tvLocation.setText(mDataList.get(position).getVenuename()                + ", " + mDataList.get(position).getVenuelocation());        holder.tvDate.setText(mDataList.get(position).getDate().trim());        holder.tvEntry.setText(mDataList.get(position).getLastEntry().trim());        holder.tvBookId.setText(mDataList.get(position).getBookid());        holder.tvCouple.setText(mDataList.get(position).getCoupleticket());        holder.tvFemale.setText(mDataList.get(position).getFemaleticket());        holder.tvMale.setText(mDataList.get(position).getMaleticket());        holder.tvVIpName.setText(mDataList.get(position).getVipName());        holder.tvVIpDes.setText(mDataList.get(position).getVipDes());        if (mDataList.get(position).getBookedprice().equals("0")) {            holder.tvPrice.setText("Free");        } else {            holder.tvPrice.setText("\u20B9 " + mDataList.get(position).getBookedprice());        }    }    @Override    public int getItemCount() {        return mDataList.size();    }    public interface MyClickListener {        public void onItemClick(View view, String str);    }    public void setOnClickListener(MyClickListener myClickListener) {        this.myClickListener = myClickListener;    }}